import axios from "axios";
import "./App.css";
import { Col, Row } from "react-bootstrap";
import { useEffect, useState } from "react";

async function sendMsg(message) {
  try {
    const response = await axios.post(
      "http://192.168.222.51:4000/send",
      { message: message },
      { timeout: 2000 }
    );
    return response.status;
  } catch (error) {
    return error.message;
  }
}

async function getMsgs() {
  try {
    const response = await axios.get("http://192.168.222.51:4000/load", {
      timeout: 2000,
    });
    return response.data;
  } catch (error) {
    return error.message;
  }
}

async function delAllMsgs() {
  try {
    const response = await axios.delete("http://192.168.222.51:4000/del", {
      timeout: 2000,
    });
    return response;
  } catch (error) {
    return error.message;
  }
}

function App() {
  const [inputValue, setInputValue] = useState("");
  const [messages, setMessages] = useState([]);

  useEffect(() => {
    const interval = setInterval(async () => {
      const loadedMsgs = await getMsgs();
      console.log(loadedMsgs);
      if (Array.isArray(loadedMsgs)) setMessages(loadedMsgs);
    }, 2000);

    return () => clearInterval(interval);
  }, []);

  const reservedCmds = {
    clear: delAllMsgs,
  };

  useEffect(() => {
    if (inputValue in reservedCmds) {
      reservedCmds[inputValue]();
      setInputValue("");
    }
  }, [inputValue]);

  async function sendMsgHandler(e) {
    e.preventDefault();
    await sendMsg(e.target.message.value);
  }

  return (
    <div className="App">
      <div className="container-fluid">
        <Row>
          <Col
            xs={12}
            xxl={3}
            className="d-flex flex-column align-items-center"
          >
            <h2 mt-3>–°–µ–∫—Ä–µ—Ç–Ω—ã–π –∫–∞–Ω–∞–ª —Å–≤—è–∑–∏...ü§´</h2>
            <form
              onSubmit={async (e) => {
                setInputValue("");
                await sendMsgHandler(e);
              }}
            >
              <input
                type="text"
                name="message"
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
              />
              <button type="submit">send</button>
            </form>
          </Col>
          <Col
            xs={12}
            xxl={9}
            className="d-flex flex-column align-items-center"
          >
            {messages.map((message, index) => (
              <h2 key={index}>{message}</h2>
            ))}
          </Col>
        </Row>
      </div>
    </div>
  );
}

export default App;
