import axios from "axios";
import "./App.css";
import { Col, Row } from "react-bootstrap";
import { useEffect, useState } from "react";

async function sendHook() {
  try {
    const response = await axios.get("http://192.168.222.51:4000", {
      timeout: 2000,
    });
    return response.data;
  } catch (error) {
    return error.message;
  }
}

function App() {
  const [loadedData, setLoadedData] = useState("");

  function hookHandler() {
    setLoadedData("");
    async function fetchData() {
      const data = await sendHook();
      setLoadedData(data);
    }
    fetchData();
  }

  useEffect(hookHandler, []);

  return (
    <div className="App">
      <div className="container-fluid">
        <Row>
          <Col
            xs={12}
            lg={3}
            className="d-flex justify-content-center align-items-center"
          >
            <button onClick={hookHandler}>get a response </button>
          </Col>
          <Col
            xs={12}
            lg={9}
            className="d-flex justify-content-center align-items-center"
          >
            <Row className="w-100" xs={1} md={2} xl={3} xxl={4}>
              <Col className="d-flex justify-content-center align-items-center">
                {loadedData}
              </Col>
              <Col className="d-flex justify-content-center align-items-center">
                Hi!
              </Col>
              <Col className="d-flex justify-content-center align-items-center">
                Hi!
              </Col>
              <Col className="d-flex justify-content-center align-items-center">
                Hi!
              </Col>
            </Row>
          </Col>
        </Row>
      </div>
    </div>
  );
}

export default App;
