import axios from "axios";
import "./App.css";
import { Col, Row } from "react-bootstrap";
import { useEffect, useState } from "react";
import Chat from "./components/Chat";

async function sendMsg(message) {
  try {
    const response = await axios.post(
      "http://192.168.222.51:4000/send",
      { message: message },
      { timeout: 2000 }
    );
    return response.status;
  } catch (error) {
    return error.message;
  }
}

async function getMsgs() {
  try {
    const response = await axios.get("http://192.168.222.51:4000/load", {
      timeout: 2000,
    });
    return response.data;
  } catch (error) {
    return error.message;
  }
}

async function delAllMsgs() {
  try {
    const response = await axios.delete("http://192.168.222.51:4000/del", {
      timeout: 2000,
    });
    return response;
  } catch (error) {
    return error.message;
  }
}

function App() {
  const [inputValue, setInputValue] = useState("");
  const [messages, setMessages] = useState(["–ó–∞–≥—Ä—É–∑–∫–∞..."]);
  const [isControls, setIsControls] = useState(false);

  useEffect(() => {
    const interval = setInterval(async () => {
      const loadedMsgs = await getMsgs();
      console.log(loadedMsgs);
      if (Array.isArray(loadedMsgs)) setMessages(loadedMsgs);
    }, 2000);

    return () => clearInterval(interval);
  }, []);

  const reservedCmds = {
    clear: delAllMsgs,
  };

  async function sendMsgHandler(e) {
    e.preventDefault();
    await sendMsg(e.target.message.value);
  }

  return (
    <div className="App">
      <div className="container-fluid">
        <Row>
          <Col
            xs={12}
            xxl={3}
            className="d-flex flex-column align-items-center"
          >
            <h2 className="mt-5">–°–µ–∫—Ä–µ—Ç–Ω—ã–π –∫–∞–Ω–∞–ª —Å–≤—è–∑–∏...ü§´</h2>
            <form
              onSubmit={async (e) => {
                setInputValue("");
                await sendMsgHandler(e);
              }}
              className="d-flex"
            >
              <input
                type="text"
                name="message"
                value={inputValue}
                onChange={(e) => {
                  const curValue = e.target.value;
                  if (curValue in reservedCmds) {
                    setInputValue("");
                    reservedCmds[curValue]();
                    return;
                  }
                  if (curValue.trim()) setInputValue(curValue);
                }}
                className="form-control"
              />
              <button type="submit" className="btn btn-dark">
                send
              </button>
            </form>
            <button
              className="mt-4 btn btn-warning"
              onClick={() => setIsControls(!isControls)}
            >
              –ê–¥–º–∏–Ω–∫–∞
            </button>
            {isControls && (
              <div className="mt-3">
                <input type="file" />
              </div>
            )}
          </Col>
          <Chat messages={messages} />
        </Row>
      </div>
    </div>
  );
}

export default App;
