import axios from "axios";
import "./App.css";
import { Col, Row } from "react-bootstrap";
import { useEffect, useState } from "react";

async function sendHook() {
  try {
    const response = await axios.get("http://192.168.222.51:4000", {
      timeout: 2000,
    });
    return response.data;
  } catch (error) {
    return error.message;
  }
}

function App() {
  const [loadedData, setLoadedData] = useState("Pending...");

  function hookHandler() {
    setLoadedData("Pending...");
    async function fetchData() {
      const data = await sendHook();
      setLoadedData(data);
    }
    fetchData();
  }

  useEffect(hookHandler, []);

  const [inputValue, setInputValue] = useState("");

  return (
    <div className="App">
      <div className="container-fluid">
        <Row>
          <Col
            xs={12}
            xxl={3}
            className="d-flex flex-column align-items-center"
          >
            <h1>controls</h1>
            <form>
              <input
                type="text"
                value={inputValue}
                onChange={(e) => setInputValue(e.target.value)}
              />
              <button type="submit"></button>
            </form>
          </Col>
          <Col
            xs={12}
            xxl={9}
            className="d-flex flex-column align-items-center"
          >
            <h1>msgs</h1>
          </Col>
        </Row>
      </div>
    </div>
  );
}

export default App;
